class Solution:
    def findMinHeightTrees(self, n: int, edges: List[List[int]]) -> List[int]:
        graph = defaultdict(list)
        for u,v in edges:
            graph[u].append(v)
            graph[v].append(u)

        @cache
        def dfs(n,p):
            # n - current node
            # p - parrent
            ans = 0
            for nb in graph[n]:
                if nb != p:
                    ans = max(ans, 1 + dfs(nb,n))
            return ans

		# distance from node till the last leaf
        c = [inf] * n
        for node in range(n):
            a = 0
            for nb in graph[node]:
                a = max(a, 1 + dfs(nb,node))
            c[node] = min(c[node], a)

        min_path = min(c)
        ans = []
        for i,v in enumerate(c):
            if v == min_path:
                ans.append(i)
        return ans
