// BFS Solution from leaves to center
// Start from the nodes whose degrees <= 1
// Only degrees[neighbor] == 1 can be added to the que during the BFS.
// if n <= 2, stop the search and remaining 1 or 2 nodes are the result.
// Note: if n == 1, degrees[0] is 0.
// Time complexity: O(N)
// Space complexity: O(N)
class Solution {
    public List<Integer> findMinHeightTrees(int n, int[][] edges) {
        if (n <= 0) return new ArrayList<>();
        int[] degrees = new int[n];
        List<Integer>[] graph = new ArrayList[n];
        buildGraph(graph, degrees, edges);
        Queue<Integer> que = new ArrayDeque<>();
        for (int i = 0; i < n; i++) {
            if (degrees[i] <= 1) {  // Note: if n == 1, degrees[0] is 0.
                que.add(i);
            }
        }
        while (n > 2) {
            int size = que.size();
            n -= size;
            for (int k = 0; k < size; k++) {
                int node = que.poll();
                for (int neighbor : graph[node]) {
                    degrees[neighbor]--;
                    if (degrees[neighbor] == 1) {
                        que.add(neighbor);
                    }
                }
            }
        }
        return new ArrayList<>(que);
    }

    private void buildGraph(List<Integer>[] graph, int[] degrees, int[][] edges) {
        for (int i = 0; i < graph.length; i++) {
            graph[i] = new ArrayList<>();
        }
        for (int[] edge : edges) {
            int u = edge[0], v = edge[1];
            graph[u].add(v);
            graph[v].add(u);
            degrees[u]++;
            degrees[v]++;
        }
    }
}
