class Solution {
public:
    vector<int> findMinHeightTrees(int n, vector<vector<int>>& edges) {
        vector<int> res;
        queue<int> myq;
        vector<unordered_set<int>> adj(n);

        if (edges.size() == 0) return {0};

        // Create adj matrix
        for (auto iter : edges) {
            adj[iter[0]].insert(iter[1]);
            adj[iter[1]].insert(iter[0]);
        }

        // Find the leaf nodes or the nodes connected to only one node
        for (int i =0; i < n; i++) {
            if (adj[i].size() == 1) {
                myq.push(i);
            }
        }

        // Keep on scrapping the leaf nodes until we reach only two nodes in the adj
        //int new_n = n;
        while(n > 2) {
            int len = myq.size();
            //new_n = new_n - len; // Removing nodes from the adj
            n = n - len;

            for (int i = 0; i < len; i++) {
                int node = myq.front();
                myq.pop();
                for (int j : adj[node]) {
                    // Remove the node from each adj node
                    adj[j].erase(node);
                    // If new size is 1 push in myq
                    if (adj[j].size() == 1) {
                        myq.push(j);
                    }
                }
            }
        }

        while (!myq.empty()) {
            res.push_back(myq.front());
            myq.pop();
        }

        return res;
    }
};
