    vector<int> findMinHeightTrees(int n, vector<vector<int>>& edges) {

        if(n<=1){                 //if the tree is empty
            vector<int> res;
            for(int i=0;i<n;i++)
                res.push_back(i);
            return res;
        }
        vector<vector<int>> adj(n);  //create a undirected grpah from given edges
        vector<int> degree(n,0);     .//degree vector to store the no. of edges incident on a node
        for(int i=0;i<edges.size();i++){
            adj[edges[i][0]].push_back(edges[i][1]);
            adj[edges[i][1]].push_back(edges[i][0]);

            degree[edges[i][0]]++;     //increase the degree of both nodes connected with the edge
            degree[edges[i][1]]++;    

        }
        queue<int> q;    
    //intially only those nodes will be processed whose degree is one  i.e, nodes connected with only one other node
        for(int i=0;i<degree.size();i++){
            if(degree[i]==1)q.push(i);
        }
  vector<int> res; //vector to store the result

  //level order traversal      
        while(!q.empty()){  
            int s = q.size();
            res.clear();

            for(int i=0;i<s;i++){
               int to =  q.front();
                q.pop();
                res.push_back(to);

                for(auto v:adj[to]){  //process the neighbour nodes of the one poped above
                    degree[v]--;   // since we are removing the edges one by one starting from the one which is connected to least no of nodes until only two nodesare left
                    if(degree[v]==1){  //if degree of any onode becomes one push them on queue
                        q.push(v);
                    }
                }
            }
        }
        return res;  //return the nodes with minimum height
    }
};
