 class Solution {
	public List<Integer> findMinHeightTrees(int n, int[][] edges) {
		//create List<Integer> to return a result
		List<Integer> res = new ArrayList<>();
		//corner case
		if(n == 1){//that vertex start from 0;
			res.add(0);
		}

		int [] degree = new int [n]; //to record each vertex degree;
		Map<Integer, List<Integer>> map = new HashMap<>();// to record each vertex connect neiborhood.

		buildGraph(edges, degree, map);

		Queue<Integer> queue = new LinkedList<>();

		for(int i = 0; i<n; i++){//add all vertex degree equals 1 
			if(degree[i] == 1){
				queue.offer(i);
			}
		}

		while(!queue.isEmpty()){
			List<Integer> tmp = new ArrayList<>();
			int size = queue.size();
			for(int i = 0; i < size; i++){//to process each level
				int vertex = queue.poll();
				tmp.add(vertex);
				for(int neibor: map.get(vertex)){//to get each vertex neiborhood;
					degree[neibor]--;
					if(degree[neibor] == 1){
						queue.offer(neibor);
					}
				}
			}
			res = tmp; //update res.
		}
		return res;
	}

	private void buildGraph(int[][] edges, int[]degree, Map<Integer, List<Integer>> map){

		for(int[] pair: edges){
			if(!map.containsKey(pair[0]) ){
				map.put(pair[0], new ArrayList<>());  
			}

			if(!map.containsKey(pair[1])){
				 map.put(pair[1], new ArrayList<>());
			}

			map.get(pair[0]).add(pair[1]);
			map.get(pair[1]).add(pair[0]);
			degree[pair[0]]++;
			degree[pair[1]]++;
		}
	}
}

