class Solution {
    public List<Integer> findMinHeightTrees(int n, int[][] edges) {
        List<Integer> mhtNodes = null;
		//Quick get out condition
        if(n<=2){
            mhtNodes = new LinkedList();
            for(int i=0; i<n; i++){
                mhtNodes.add(i);
            }
            return mhtNodes;
        }

        List<Set<Integer>> adjacencyList = new ArrayList<>();
        Set<Integer> leaves = new HashSet<>();
        for(int i=0; i<n; i++){
            adjacencyList.add(i, new HashSet<>());
        }

		//Generate adjacency list and find out leaf nodes
        for(int[] edge: edges){
            adjacencyList.get(edge[0]).add(edge[1]);
            adjacencyList.get(edge[1]).add(edge[0]);

            if(adjacencyList.get(edge[0]).size() == 1){
                leaves.add(edge[0]);
            }else{
                leaves.remove(edge[0]);
            }

            if(adjacencyList.get(edge[1]).size() == 1){
                leaves.add(edge[1]);
            }else{
                leaves.remove(edge[1]);
            }
        }

        //Keep removing leaf nodes layer by layer. Like peeling an onion :). The innermost nodes are the aswer
        while(!leaves.isEmpty()){
            mhtNodes = new LinkedList<>(leaves);
            leaves.clear();
            for(int node: mhtNodes){
                for(int connectedNode: adjacencyList.get(node)){
                    adjacencyList.get(connectedNode).remove(node);
                    if(adjacencyList.get(connectedNode).size() == 1){
                        leaves.add(connectedNode);
                    }
                }
            }
        }
        return mhtNodes;

    }
}
