class Solution {

    public List<Integer> findMinHeightTrees(int n, int[][] edges) {
        // edge case
        if (n <= 1) return Arrays.asList(new Integer[]{0});

        ArrayList<Integer>[] graph = new ArrayList[n];
        // filling empty graph with empty list of connections
        for (int i = 0; i < n; i ++) {
            graph[i] = new ArrayList<Integer>();
        }
        // fill connections, connect one to another and vice versa;
        for (int i = 0; i < edges.length; i++) {
            int[] edge = edges[i];
            graph[edge[0]].add(edge[1]); 
            graph[edge[1]].add(edge[0]); 
        }
        // exclude actually all nodes with 1 connection
        // as they might be the source of max height tree
        Queue<Integer> queue = new LinkedList<>();
        for (int i = 0; i < n; i++) {
            if (graph[i].size() == 1) {
                queue.add(i);
            }
        }
        // while resulting amount of nodes not redused to 2 or 1
        while ( n > 2) {
            int size = queue.size();
            n -= size;
            while (size > 0) {
                // removing connections one by one
				// each vertex in a queue already had one coonnection
                Integer curr = queue.poll();
				// this is that one connection
                Integer next = graph[curr].get(0);
				// remove current vertex from connection list
                graph[next].remove(curr);
				// if connection list reduced to one vertex - add it to the queue
                if (graph[next].size() == 1) {
                    queue.add(next);
                }
				// clear current vertext connections.
                graph[curr].clear();
                size--;
            }
        }
        return (List) queue;       
    }
}
