class Solution:
    def findMinHeightTrees(self, n: int, edges: List[List[int]]) -> List[int]:
        if n == 1:
            return [0]
        graph = [[] for _ in range(n)]
        degree = [0] * n
        for a, b in edges:
            graph[a].append(b)
            graph[b].append(a)
            degree[a] += 1
            degree[b] += 1
        cur = [i for i in range(n) if degree[i] == 1]
        while cur:
            nxt = []
            for node in cur:
                degree[node] -= 1
                for _node in graph[node]:
                    degree[_node] -= 1
                    if degree[_node] == 1:
                        nxt.append(_node)
            if not nxt: # if nxt is empty, it means cur is the result.
                return cur
            cur = nxt
