class Solution {
public:
    string intToRoman(int num) {
        string romans[] = {"I","IV","V","IX","X","XL","L","XC","C","CD","D","CM","M"};
        int numerals[]={1,4,5,9,10,40,50,90,100,400,500,900,1000};
        string res;
        while(num)
        {
            for(int i=12;i>=0;i--)
            {
                int x = num/numerals[i];
                if(x)
                {
                    while(x--)
                    {
                        res += romans[i];
                    }
                    num %= numerals[i];
                    break;
                }
            }
        }   
        return res;
    }
};
