```C++ []
class Solution {
public:
    string intToRoman(int num) {
        return convertRoman(num/1000, 4) + convertRoman((num%1000)/100, 3) + convertRoman((num%100)/10, 2) + convertRoman(num%10, 1);      
    }

private:
    int values[7] { 1000, 500, 100, 50, 10, 5, 1 };
    string strs[7] { "M", "D", "C", "L", "X", "V", "I" };
    unordered_map<int, int> myDict {{4, 0}, {3, 2}, {2, 4}, {1, 6}};

    string convertRoman(int digit, int value) {
        string res = "";
        int temp = myDict[value];
        if (temp == 0) 
            res = repeat(strs[temp], digit);
        else {
            if (digit >= 1 && digit < 4) 
                res += repeat(strs[temp], digit);
            else if (digit == 4) 
                res += strs[temp] + strs[temp - 1];
            else if (digit >= 5 && digit < 9) 
                res += strs[temp - 1] + repeat(strs[temp], (digit - 5));
            else if (digit == 9) 
                res += strs[temp] + strs[temp - 2];
        }
        return res;
    }

    string repeat(string repeatVal, int num) {
        string ret = "";
        while (num--) 
            ret += repeatVal;
        return ret;
    }
};
