    static bool hasPathSum(const TreeNode* root, int targetSum) {
        if (!root) return false;

        stack<pair<const TreeNode*, int>> st;
        st.push({root, targetSum});

        while (!empty(st)) {
            const auto [node, target] = st.top(); st.pop();
            if (!node->left && !node->right && node->val == target) return true;

            if (node->left) st.push({node->left, target - node->val});
            if (node->right) st.push({node->right, target - node->val});
        }

        return false;
    }
