public class Solution {
    public bool HasPathSum(TreeNode root, int targetSum) {
        return Search(root, targetSum);
    }

    public bool Search(TreeNode node, int targetSum) {
        if (node == null) {
            return false;
        }

        // Determine the current sum
        targetSum = targetSum - node.val;

        // Since this is a root-to-leaf check, evaluate for the 
        // success condition when both left and right nodes are null        
        if (node.left == null && node.right == null) {
            return targetSum == 0;
        }

        // Keep exploring along branches finding the target sum
        return Search(node.left, targetSum)
            || Search(node.right, targetSum);      
    }    
}
```

