    pub fn has_path_sum(root: Option<Rc<RefCell<TreeNode>>>, sum: i32) -> bool {
        Solution::helper(&root, sum)
    }

    fn helper(root: &Option<Rc<RefCell<TreeNode>>>, sum: i32) -> bool {
        if let Some(data) = root {
            let node = data.borrow_mut();
            if node.left.is_none() && node.right.is_none() { // is leaf
                return sum == node.val;
            }
            return Solution::helper(&node.left, sum - node.val)
                || Solution::helper(&node.right, sum - node.val);
        }
        false
    }
