        if (ptr < arr.length) {
            ptr  = fromConsumeOne(focus, ptr, arr,q,Position.Right);
        }

        return ptr;
    }

    public static TreeNode from(Integer[] arr) {
        return from2(arr);

    }

    static int fromConsumeOne(TreeNode focus, int ptr, Integer[] arr, Queue<TreeNode> q, Position position) {
        if (arr[ptr] == null) {
            ptr++;
        } else {
            final var node = new TreeNode(arr[ptr++]);
            q.add(node);
            if (position == Position.Left) {
                focus.left = node;
            } else {
                focus.right = node;
            }
        }
        return ptr;
    } 
