class Trie {
    public:
    trie *root;
    Trie() {
        root = new trie();
    }
    void insert(string &word)
    {
        int n = word.size();
        trie *temp = root;
        for(int i=0; i<n; i++)
        {
            if(temp->arr[word[i]-'a'] == NULL)
            {
                temp->arr[word[i]-'a'] = new trie();
            }
            temp = temp->arr[word[i]-'a'];
        }
        temp->end = true;
		// making use of string only here since we don't need it otherwise
        temp->s = word;
    }
};
