```
class Solution {
public:
	// we can move in all 4 directions in very less line(s) of code using this
    vector<vector<int>> moves{{0,0,-1,1}, {1,-1,0,0}};

	//our final ans
    vector<string> ans;

	//main function
    void dfs(vector<vector<char>>& board, vector<string>& s, trie* ptr, int i, int j)
    {
        if(i==-1 || i==board.size() || j==-1 || j==board[0].size() || board[i][j]=='#') return;

		//traversing towards the character board[i][j] of the string (if present)
		//this will be the first character of string when executed the first time (from the main original function)
        ptr = ptr->arr[board[i][j]-'a'];

		//if no such word exist then we simply return
        if(!ptr) return;

		//if this is a end that means a word exists so we append it to our ans
        if(ptr->end) 
        {
            ptr->end = false;
            ans.push_back(ptr->s);
        }

		//since we can't come to a cell once traversed 
        char el = board[i][j];
		// we make it unusable for mean time
        board[i][j] = '#';

		//traversing in all 4 directions with a single line of code !!
        for(int x=0; x<4; x++) dfs(board, s, ptr, i+moves[0][x], j+moves[1][x]);

		//backtracking
        board[i][j] = el;
    }
    vector<string> findWords(vector<vector<char>>& board, vector<string>& s) {
        int n = board.size(), m = board[0].size(), ss = s.size();
        Trie obj
		//building the trie
        for(int k=0; k<ss; k++)
        {
            obj.insert(s[k]);
        }
		//instead of traversing for all words we just traverse one time across the whole board
        for(int i=0; i<n; i++)
        {
            for(int j=0; j<m; j++)
            {
                dfs(board, s, obj.root, i, j);
            }
        }
        return ans;
    }
};
```
A more cleaner approach:
```
class Trie {
    public:
        unordered_map<char, Trie*> child;
        bool isEnd = false;
        string word;
};
class Solution {
public:
    Trie* root = new Trie();

    void insertWord(string &s)
    {
        Trie* temp = root;
        for(char c : s)
        {
            if(temp->child.find(c) == temp->child.end())
                temp->child[c] = new Trie();
            temp = temp->child[c];
        }
        temp->isEnd = true;
        temp->word = s;
    }
    vector<string> ans;
    bool isValid(int i, int j, int n, int m)
    {
        return (i >= 0 and j >= 0 and i < n and j < m);
    }
    void rec(int i, int j, int n, int m, vector<vector<char>> &board, vector<vector<bool>> &vis, Trie* temp)
    {
        if(temp->isEnd)
        {
            temp->isEnd = false;
            ans.push_back(temp->word);
        }
        int dir[5] = {-1, 0, 1, 0, -1};

        for(int k=0; k<4; ++k)
        {
            int new_i = i + dir[k];
            int new_j = j + dir[k+1];

            if(isValid(new_i, new_j, n, m) && temp->child.find(board[new_i][new_j]) != temp->child.end() && !vis[new_i][new_j])
            {
                vis[new_i][new_j] = true;

                rec(new_i, new_j, n, m, board, vis, temp->child[board[new_i][new_j]]);

                vis[new_i][new_j] = false;
            }
        }
    }
    vector<string> findWords(vector<vector<char>>& board, vector<string>& words) {
        for(auto s : words)
            insertWord(s);

        int n = board.size(), m = board[0].size();
        vector<vector<bool>> vis(n, vector<bool>(m));

        for(int i=0; i<n; ++i)
        {
            for(int j=0; j<m; ++j)
            {
                if(root->child.find(board[i][j]) != root->child.end())
                {
                    Trie* temp = root;
                    vis[i][j] = true;
                    rec(i, j, n, m, board, vis, temp->child[board[i][j]]);
                    vis[i][j] = false;
                }
            }
        }
        return ans;
    }
};
```

