    class Node{
        public:
        Node* links[26]={NULL};
        string flag;
        void put(char ch,Node* node){
            links[ch-'a']=node;
        }
        Node* next(char ch){
            return links[ch-'a'];
        }
        void set(string w){
            flag=w;
        }
        int isEnd(){
            return flag.length();
        }
    };
    int dr[4] = {1, -1, 0, 0}; 
    int dc[4] = {0, 0, -1, 1}; 
    int n,m;
    void dfs(int r, int c, vector<vector<char>>& grid, Node *trie, vector<string>&ans) {
        if (r < 0 || r >= n || c < 0 || c >= m || grid[r][c]=='0' || trie->next(grid[r][c])==nullptr) return;
