# Approach
<!-- Describe your approach to solving the problem. -->
Apply dfs from every cell to find a path that correspond to a string and use trie to check if it is a required string.

# Code
```
class Solution {
    struct trie {
        trie *child[26];
        bool isEnd;
        trie() {
            for(int i = 0; i < 26; i++) child[i] = NULL;
            isEnd = false;
        }
    };
    trie *root = new trie();
    void insert(string s) {
        trie *curr = root;
        for(int i = 0; i < s.size(); i++) {
            int id = s[i] - 'a';
            if(curr->child[id] == NULL) curr->child[id] = new trie();
            curr = curr->child[id];
        }
        curr->isEnd = true;
    }
    vector<vector<char>> board; int n, m; vector<vector<bool>> vis; vector<string> ans;
    string str;
    void dfs(trie *curr, int y, int x) {
        if(curr->isEnd) {
            ans.push_back(str);
            curr->isEnd = false;
        }
        if(y < 0 || y >= n || x < 0 || x >= m || vis[y][x]) return;
        int id = board[y][x] - 'a';
        if(curr->child[id] == NULL) return;
        vis[y][x] = true;
        str.push_back(board[y][x]);
        dfs(curr->child[id], y + 1, x);
        dfs(curr->child[id], y - 1, x);
        dfs(curr->child[id], y, x + 1);
        dfs(curr->child[id], y, x - 1);
        str.pop_back();
        vis[y][x] = false;
    }
public:
    vector<string> findWords(vector<vector<char>>& board, vector<string>& words) {
        for (auto &word: words) insert(word);
        this->board = board; n = board.size(); m = board[0].size();
        vis.resize(n, vector<bool>(m, false));
        for(int i = 0; i < n; i++) {
            for(int j = 0; j < m; j++) {
                dfs(root, i, j);
            }
        }
        return ans;
    }
};
