        if(curr->child[id] == NULL) return;
        vis[y][x] = true;
        str.push_back(board[y][x]);
        dfs(curr->child[id], y + 1, x);
        dfs(curr->child[id], y - 1, x);
        dfs(curr->child[id], y, x + 1);
        dfs(curr->child[id], y, x - 1);
        str.pop_back();
        vis[y][x] = false;
    }
public:
    vector<string> findWords(vector<vector<char>>& board, vector<string>& words) {
        for (auto &word: words) insert(word);
        this->board = board; n = board.size(); m = board[0].size();
        vis.resize(n, vector<bool>(m, false));
        for(int i = 0; i < n; i++) {
            for(int j = 0; j < m; j++) {
                dfs(root, i, j);
            }
        }
        return ans;
    }
};
