                    flag = False
                    del node
            if flag:
                visited.add((r,c))
                dfs(r-1,c,node,currWord)
                dfs(r+1,c,node,currWord)
                dfs(r,c-1,node,currWord)
                dfs(r,c+1,node,currWord)
                visited.remove((r,c))

        for r in range(ROWS):
            for c in range(COLS):
                dfs(r,c,root,"")

        return list(result)

