class Solution {

    bool dfs(vector<vector<char>>& board, const string& s,
             unsigned int idx, int i, int j) {
        if(idx == s.length()) {
            return true;
        }

        //Check if we are in bounds.
        if(i<0 or j<0) return false;
        if(i>=(int)board.size() or j>=(int)board[0].size()) return false;

        //Check if we found the right character/
        if(board[i][j] != s[idx]) {
            return false;
        }

        //Remember which character was at this location,
        //then assign a dummy character, to prevent using
        //the same character twice.
        char c = board[i][j];
        board[i][j] = '*';

        //Search for the next character at the adjacent indexes.
        bool good = dfs(board, s, idx+1, i+1, j) or
                     dfs(board, s, idx+1, i-1, j) or
                     dfs(board, s, idx+1, i, j+1) or
                     dfs(board, s, idx+1, i, j-1);

        //Change the character back and return the result.
        board[i][j] = c;
        return good;
    }
public:
    typedef pair<int,int> Pair;
    vector<string> findWords(vector<vector<char>>& board, vector<string>& words) {
        unordered_map<char,vector<Pair>> letters;
        vector<string> found;

        //Record the indexes of each char.
        for(int i = 0; i < (int)board.size(); ++i) {
            for(int j = 0; j < (int)board[0].size(); ++j) {
                letters[board[i][j]].push_back(Pair(i,j));
            }
        }

        for(string s: words) {
            bool cont = false;

            //If we didn't already find every character in s,
            //we can't possibly find the word, so we move on.
            for(auto i = 0u; i < s.length(); ++i) {
                if(letters.find(s[i]) == letters.end()) {
                    cont = true;
                    break;
                }
            }

            if(cont)
                continue;

            //We skip to the indexes of the chars that are
            //equal to the first character in s.
            for(Pair p: letters[s[0]]) {
                if(dfs(board, s, 0u, p.first, p.second)) {
                    found.push_back(s);
                    break;
                }
            }
        }

        return found;
    }
};
