class TrieNode{
public:
    bool is_end;
    vector<TrieNode*> children;
    TrieNode(){
        is_end=false;
        children=vector<TrieNode*>(26, NULL);
    }   
};
class Solution {
public:
    vector<string> findWords(vector<vector<char>>& board, vector<string>& words) {
        root=new TrieNode();
        for(int i=0;i<words.size();i++)
        {
            TrieNode* cur=root;
            for(int j=0;j<words[i].size();j++)
            {
                int k=words[i][j]-'a';
                if(cur->children[k]==NULL)
                    cur->children[k]=new TrieNode();
                cur=cur->children[k];
            }
            cur->is_end=true;
        }
        vector<string> ans;
        for(int i=0;i<board.size();i++)
        {
            for(int j=0;j<board[0].size();j++)
            {
                search(board, root, "", ans, i, j);
            }
        }

        return ans;
    }

    void search(vector<vector<char>>& board, TrieNode* root, string word, vector<string>& s, int x, int y)
    {
        if(root->is_end)
        {
            s.push_back(word);
            root->is_end = false;
        }
        if(x<0 || y<0 || x>=board.size() || y>=board[0].size() || board[x][y]=='.')
            return;
        char c=board[x][y];
        int k=c-'a';
        if(root->children[k]==NULL)
            return;
        root=root->children[k];
        board[x][y]='.';
        word+=c;
        search(board, root, word, s, x+1, y);
        search(board, root, word, s, x-1, y);
        search(board, root, word, s, x, y+1);
        search(board, root, word, s, x, y-1);
        board[x][y]=c;
    }
    TrieNode* root;
};
```
