        TreeNode *root = new TreeNode();
        for (int j = 0; j < words.size(); j++) {
            string word = words[j];
            TreeNode *curr = root;
            for (int i = 0; i < word.length(); i++) {
                char c = word[i] - 'a';
                if (curr->children[c] == nullptr) {
                    curr->children[c] = new TreeNode();
