***Updated solution using Trie***

```
public class Solution {
    public IList<string> FindWords(char[][] board, string[] words) {
        var result = new List<string>();

        // Add words to the trie
        var trie = new Trie();
        foreach (var word in words) {
            trie.Insert(word);
        }

        // Start at the root node and explore as far as possible along each branch
        var root = trie.GetRoot();
        for (int rowIndex = 0; rowIndex < board.Length; ++rowIndex) {
            for (int colIndex = 0; colIndex < board[rowIndex].Length; ++colIndex) {
                DFS(board, rowIndex, colIndex, root, result);
            }
        }

        return result;
    }

    void DFS(char[][] board, int row, int col, TrieNode node, List<string> result) {
        // Make sure the search paramaters are in bounds
        if (row < 0 || row >= board.Length || col < 0 || col >= board[row].Length) {
            return;            
        }
        // Check to see if this row has been visited
        var rowVisitedMarker = '@';
        if (board[row][col] == rowVisitedMarker) {
            return;            
        } 
        // Check to see if this character exists in the trie
        if (!node.ContainsKey(board[row][col])) {
            return;            
        }    

        // Get the trie node for this char
        node = node.Get(board[row][col]);

        if (!node.visited && !string.IsNullOrEmpty(node.word)) {
            result.Add(node.word);
            node.visited = true;      
        }

        // Mark that this row has been visited
        var oldValue = board[row][col];        
        board[row][col] = rowVisitedMarker;

        // Search left
        DFS(board, row, col - 1, node, result);

        // Search right
        DFS(board, row, col + 1, node, result);

        // Search top
        DFS(board, row - 1, col, node, result);

        // Search bottom
        DFS(board, row + 1, col, node, result);

        // Unmark that this row has been visited
        board[row][col] = oldValue;
    }   


    // Trie
    public class Trie {
        private TrieNode root;

        public Trie() {
            root = new TrieNode();
        }
        public TrieNode GetRoot() {
            return root;
        }
        public void Insert(string word) {
            var node = root;
            foreach (var ch in word) {
                if (!node.ContainsKey(ch)) {
                    node.Add(ch, new TrieNode());
                }
                node = node.Get(ch);
            }
            node.word = word;
        }
    }

    // TrieNode
    public class TrieNode {
        private Dictionary<char, TrieNode> children;
        public string word = string.Empty;
        public bool visited = false;

        public TrieNode() {
            children = new Dictionary<char, TrieNode>();
        }
        public bool ContainsKey(char ch) {
            return children.ContainsKey(ch);
        }
        public TrieNode Get(char ch) {
            return children[ch];
        }
        public void Add(char ch, TrieNode node) {
            children[ch] = node;
        }
    }
}
