  * WIll post the trie solution soon.

 */
class Solution {
    private  boolean[][] visited;
    int nRow, nCol;
    Set<String> result;
    int[] xs = {0, -1, 0, 1};
    int[] ys = {-1, 0, 1, 0};



    /**
     * Check for each cell if the char matches then do a dfs if not back track search for other directions
     * if none of matches then return and try for another cell from the start.
     * @param board
     * @param words
     * @return
     */
    public List<String> findWords(char[][] board, String[] words) {
        nRow=board.length;
        nCol=board[0].length;
        visited=new boolean[nRow][nCol];
        result=new HashSet<>();
        //running for each cell
        for(String word: words) {//for each word
            for (int i = 0; i < nRow; i++) {
                for (int j = 0; j < nCol; j++) {
                    dfs(board, word, i, j, 0);
                }
            }
        }
        return new ArrayList<>(result);
    }

     /**
     * DFS for an array
     * @param board
     * @param word
     * @param x
     * @param y
     * @param indexWord
     */
     private void dfs(char[][] board,String word,int x,int y,int indexWord){
        if(indexWord>=word.length())
            return;
        //to check if word is found or not
        if(indexWord==word.length()-1 && word.charAt(indexWord)==board[x][y]) {
            result.add(word);
            return ;
        }


        //boundary condition so that dfs doesnt go out of the grid
       // else if(x> nRow-1 || y> nCol-1 || x<0 || y<0 || word.charAt(indexWord)!=board[x][y])
        else if(word.charAt(indexWord)!=board[x][y])
            return ;
      //mark as visited
        visited[x][y]=true;
        //for all 4 directions call dfs
        for(int i=0;i<4;i++){
            int row=x+xs[i];
            int col=y+ys[i];
            //check to see if DFS can be called or not
            if(row<= nRow-1 && col<= nCol-1 && row>=0 && col>=0 && !visited[row][col])
                dfs(board,word,row,col,indexWord+1);
        }
        //reset for back track
        visited[x][y]=false;
    }
}

