class Solution {    
    struct node{    //TrieNode
        char c;
        int end;
        string word;
        node *child[26];
    };
    struct node *getNode(char c)    //get newnode
    {
        node *newnode = new node;
        newnode->c = c;
        newnode->end = 0;
        newnode->word = "";
        for(int i=0;i<26;++i)
            newnode->child[i] = NULL;
        return newnode;
    }
    node *root = getNode('/');  //root

    //Trie INSERT
    void insert(string s)
    {
        node *cur = root;
        int index,i=0;
        while(s[i])
        {
            index = s[i]-'a';
            if(cur->child[index]==NULL){
                cur->child[index] = getNode(s[i]);
            } 
            cur=cur->child[index];
            i++;
        }
        cur->end += 1;
        cur->word = s;
    }

    void solve(vector<vector<char>>& board,int i,int j,vector<string>& ans,node *cur) {
        int r = board.size();
        int c = board[0].size();
        //Base case
        //If the trie doesn't have the current char OR cell is Visited
        int index = board[i][j]-'a';
        if(board[i][j]=='#' || cur->child[index]==NULL)  
            return;

        cur = cur->child[index];
        if(cur->end > 0)
        {
            ans.push_back(cur->word);
            cur->end -=1;
        }

        //Body
        char ch = board[i][j];   //Store current char
        board[i][j] = '#';  //Mark current node visited

        if(i>0)     //TOP
            solve(board,i-1,j,ans,cur);
        if(i<r-1)   //DOWN
            solve(board,i+1,j,ans,cur);
        if(j>0)     //LEFT
            solve(board,i,j-1,ans,cur);
        if(j<c-1)   //RIGHT
            solve(board,i,j+1,ans,cur);

        board[i][j] = ch;    //Mark current node as Unvisited by restoring the value
    }

public:
    vector<string> findWords(vector<vector<char>>& board, vector<string>& words) {        
        int r=board.size();
        int c=board[0].size();

        //Insert all words in TRIE
        for(int i=0;i<words.size();++i){
            insert(words[i]);
        }


        //Now search words
        vector<string> ans;
        for(int i=0;i<r;++i) {
            for(int j=0;j<c;++j) {
                solve(board,i,j,ans,root);
            }
        }
        return ans;
    }
};


