        self.root = TrieNode()

    def insert(self, word):
        node = self.root
        for char in word:
            if char not in node.children:
                node.children[char] = TrieNode()
            node = node.children[char]
        node.isEnd = True

class Solution:
    def findWords(self, board: List[List[str]], words: List[str]) -> List[str]:
        def backtrack(node, curr, i, j):
            for char in node.children:
                if board[i][j] != char:
                    continue
                curr += char
                visited.add((i, j))
                nxt = node.children[char]
                if nxt.isEnd:
                    res.add(curr)
                for di, dj in [(0, 1), (1, 0), (0, -1), (-1, 0)]:
                    i1, j1 = i + di, j + dj
                    if 0 <= i1 < m and 0 <= j1 < n and (i1, j1) not in visited:
                        backtrack(nxt, curr, i1, j1)
                visited.remove((i, j))
                curr = curr[:-1]

        if not board or not board[0] or not words:
            return []  
        m, n = len(board), len(board[0])
        res = set()
        trie = Trie()
        for word in words:
            trie.insert(word)
        visited = set()
        for i in range(m):
            for j in range(n):
                backtrack(trie.root, '', i, j)
        return list(res)
