class Solution {
public:
    /*
    4 parts
    TrieNode definition and declaration of root
    dfs traversal logic
    insert word logic
    main function
    */
    //new trienode() ka syntax matlab?
    struct TrieNode
    {
        TrieNode* child[26];//node pointing to other 26 possible nodes...if they dont exist then null
        bool flag=false;//for knowing if the node is the end of a possible word or not
        bool Inserted=false;//to avoid duplicates in ans...if the word already inserted
        string str;//keeping record of string that has made its way to here
        bool containChar(char character)
        {
            return (child[character-'a']!=NULL);//to see if the node contains the character as next node
        }
        void putNode(char nxtchar,TrieNode* node)
        {
            child[nxtchar-'a']=node;//if next character doesnt exist in trie the to insert
        }
        TrieNode* getNode(char character)
        {
            return child[character-'a'];//maybe traverse the trie further if we get our way in dfs
        }
        void setEnd()
        {
            flag=true;//sets end tag to node denoting it being end of one word
        }
        bool isEnd()
        {
            return flag;//to know if its the end or not
        }
        bool didInsert()
        {
            return Inserted;//self explanatory
        }
        void setdidInsert()
        {
            Inserted=true;//      "
        }
    };
    TrieNode* root=new TrieNode();//publicly define root of our Trie
    void insertWord(string word)//insert words from vector given into our Trie
    {
        TrieNode* node=root;
        int i;
        for(i=0;i<word.size();i++)
        {
            if(node->containChar(word[i]) ==false)
            {
                node->putNode(word[i],new TrieNode());//if not present then insert
            }
            node=node->getNode(word[i]);//traverse further in trie (now that the character is inserted if it didnt exist before)
        }
        node->setEnd();//set end to final node after traversal of word
        node->str=word;//assign str
    }
    void dfs(vector<vector<char>>&board,int i,int j,TrieNode* node,vector<string>&ans,vector<vector<bool>>&visited)
    {
        if((i<0)||(j<0)||(i>board.size()-1)||(j>board[0].size()-1))
        {
            return;//since while doing dfs we call in all four directions
            //like this we dont have to take caution while calling dfs in which directions
        }
        if(visited[i][j])
        {
            return;//if already visited then return
        }
        node=node->getNode(board[i][j]);
        if(node==NULL)
        {
            return;//if the node for that further character doesnt exist
        }
        if(node->isEnd() && !(node->Inserted))
        {
            ans.push_back(node->str);//if is end and not inserted before
            node->Inserted=true;//so no re pushing of same word back possibly
        }
        visited[i][j]=true;
        dfs(board,i+1,j,node,ans,visited);
        dfs(board,i,j+1,node,ans,visited);
        dfs(board,i-1,j,node,ans,visited);
        dfs(board,i,j-1,node,ans,visited);//calling in all 4 directions
        visited[i][j]=false;//while backtracking in dfs...it re assigns the previous value of false to assigned ones
    }
    vector<string> findWords(vector<vector<char>>& board,vector<string>& words) 
    {
        vector<string>ans;
        for(string word:words)
        {
            insertWord(word);//makes our Trie/Inserting words one by one
        }
        vector<vector<bool>>visited(board.size(),vector<bool>(board[0].size(),false));//to avoid infinite looping in dfs and keep record of visited ones
        // size, value(size,value)
        int i,j;
        for(i=0;i<board.size();i++)
        {
            for(j=0;j<board[0].size();j++)//traverse matrix and check for all words but efficiently bcoz of trie
            {
                dfs(board,i,j,root,ans,visited);
                // board to traverse,i and j to know current position, root to check in trie,push word if found,visited to keep record
            }
        }
        return ans;
    }
};
