**Approach 1: (DFS + Map) TLE**
```
class Solution {
public:
    int n,m;
    void search(int i, int j, int ind, vector<vector<char>>& board, string &word, bool &found)
    {
        if(board[i][j]=='1' || found)     return;
        if(ind == word.size())
        { 
            found = true;
            return;
        }
        char temp = board[i][j];
        board[i][j] = '1';
        if(i-1>=0 && board[i-1][j]==word[ind]) search(i-1,j,ind+1,board,word,found);
        if(i+1<n && board[i+1][j]==word[ind]) search(i+1,j,ind+1,board,word,found);
        if(j+1<m && board[i][j+1]==word[ind]) search(i,j+1,ind+1,board,word,found);
        if(j-1>=0 && board[i][j-1]==word[ind]) search(i,j-1,ind+1,board,word,found);
        board[i][j] = temp;
    }
    vector<string> findWords(vector<vector<char>>& board, vector<string>& words) {
        unordered_map<char,vector<string>>mp;
        vector<string> ans;
        n = board.size();
        m = board[0].size();
        for(int i=0;i<words.size();i++)     mp[words[i][0]].push_back(words[i]);
        for(int i=0;i<n;i++)
        {
            for(int j=0;j<m;j++)
            {
                if(mp[board[i][j]].size() > 0)
                {
                    for(auto m:mp[board[i][j]])
                    {
                        bool found = false;
                        search(i,j,1,board,m,found);
                        if(found && find(ans.begin(), ans.end(), m)==ans.end()) 
                            ans.push_back(m);

                    }
                }
            }
        }
        return ans;
    }
};
