```cpp
class Solution {
private:
    //DFS :  if we get word of first letter seach all possible path upto its length
    bool searchWord(vector<vector<char>>&board,long long i,long long j,long long count,string word){
        //Base case : we get all the char of words 
        if(count==word.length()){
            return true;
        }

        //case : out of bound like i and j must be in board and word[count]==bord[i][j] other wise false return false
        if(i<0 or i>=board.size() or j<0 or j>=board[i].size() or board[i][j]!=word[count]){
            return false;    
        }

        //mark for not visiting duplicate :
        char temp = board[i][j];
        board[i][j]=' ';

        //search for possible paths :
        bool found = searchWord(board,i+1,j,count+1,word) ||  //right
					 searchWord(board,i-1,j,count+1,word) ||  //left
					 searchWord(board,i,j+1,count+1,word)||  //down
					 searchWord(board,i,j-1,count+1,word);   //up 

        //unmark the word for next call  : 
        board[i][j] = temp;

        return found; 
    }
public:
    vector<string> findWords(vector<vector<char>>& board, vector<string>& words) {
        vector<string>ans;
        string temp;
        for(int w=0;w<words.size();w++){
            bool flag=false;
            temp=words[w];
            reverse(temp.begin(),temp.end());
            for(long long i=0;i<board.size();i++){
                for(long long j=0;j<board[i].size();j++){
                    if(board[i][j] == temp[0] and searchWord(board,i,j,0,temp)){
                        reverse(temp.begin(),temp.end());
                        ans.push_back(temp);
                        flag=1;
                        break;
                    }
                }
                if(flag==true){
                    break;
                }
            }
        }    
        return ans;
    }
};
