        if (cur != null && cur.word != null) {
            res.add(cur.word);
            subtract(cur.word);
            cur.word = null;
        }

        char temp = board[i][j];
        board[i][j] = '@';
        for (int[] dir: dirs) {
            int x = i + dir[0];
            int y = j + dir[1];
            dfs(board, x, y, cur, res);
        }
        board[i][j] = temp;
    }

