// NOTE: we have used inserted flag in trie node to make sure
// that one word is inserted into ans vec only once o.w below 
// commented testcase fails in which "oa" is inserted twice.
// trie node class
class TrieNode {
public:
    bool end, inserted;
    vector<TrieNode*> letters;
    TrieNode (bool end=false, bool inserted=false, int size=26) {
        this->end = end;
        this->inserted = inserted;
        this->letters = vector<TrieNode*>(size, NULL);
    }
};
// soluton class
class Solution {
    typedef TrieNode node;
public:
    // trie
    node* troot = NULL;
    node* insertWord(node *root, string &word, int i=0) {
        if (!root) return insertWord(new node(), word, i);
        if (i == word.size()) root->end = true;
        else root->letters[word[i] - 'a'] = insertWord(root->letters[word[i] - 'a'], word, i + 1);
        return root;
    }
    // solution
    char fill = '#';    // assuming # doesn't exist in board
    vector<int> dx = {1, 0, -1, 0}, dy = {0, 1, 0, -1};
    string word;
    vector<string> ans;
    /*  testcase:
    ["o","a","b","n"]
    ["o","t","a","e"]
    ["a","h","k","r"]
    ["a","f","l","v"]
    ["oa","oaa"]
    */
    // main func.
    vector<string> findWords(vector<vector<char>>& board, vector<string>& words) {
        // IDEA is to use trie to optimise T(n) as it can be possible that in words list
        // we have prefixes present repeatedly of a word. Eg: ['u', 'um', 'ume', 'umes', 'umesh']
        // in that case, searching words one by one is not good method, instead, search from prefix to word
        // which can be done using help of trie. Trie also make sure that only unique words are searched i.e
        // if list is: ['o', 'o', 'o', oo'] trie will be: ['o', 'oo'] only.
        // build Trie
        for (auto &word: words)
            troot = insertWord(troot, word);
        // build answer using dfs
        for (int row = 0; row < board.size(); row++)
            for (int col = 0; col < board[0].size(); col++)
                if (troot->letters[board[row][col] - 'a'])
                    dfs(board, row, col, troot);
        return ans;
    }
    void dfs(vector<vector<char>> &board, int x, int y, node *root) {
        // base case
        if (!root or x < 0 or x >= board.size() or y < 0 or y >= board[0].size())
            return;
        // below cond.: board[x][y] == fill is IMP
        if (board[x][y] == fill or !root->letters[board[x][y] - 'a'])
            return;
        // build word
        word.push_back(board[x][y]);
        // update root
        root = root->letters[board[x][y] - 'a'];
        // if word exist in trie then push it to result and mark its inserted as true
        // so that this word is not inserted again into the result
        if (root->end and not root->inserted) {
            root->inserted = true;
            ans.push_back(word);
        }
        // save the original cell and fill it
        char orig = board[x][y];
        board[x][y] = fill;
        for (int d = 0; d < 4; d++)
            dfs(board, x + dx[d], y + dy[d], root);
        // Backtrack
        board[x][y] = orig;
        word.pop_back();
    }
};
```

