class Solution {
    func findWords(_ board: [[Character]], _ words: [String]) -> [String] {
        var root = TrieNode()
        // Building the Trie
        for word in words {
            var node = root
            for char in word {
                if let child = node.children[char] {
                    node = child
                } else {
                    let newNode = TrieNode()
                    node.children[char] = newNode
