class Trie:
    WORD = '' # use empty string to mark end of a word

    def __init__(self):
        self.root = {}

    def insert(self, word: str) -> None:
        node = self.root
        for char in word:
            if char not in node:
                node[char] = {}
            node = node[char]
        node[self.WORD] = word # save the word itself to node['']


class Solution:
    def findWords(self, board: List[List[str]], words: List[str]) -> List[str]:
        results = set()
        m = len(board)    
        n = len(board[0])

        trie = Trie()
        for word in words:
            trie.insert(word)

        def backtrack(i: int, j: int, node: dict) -> None:
            char = board[i][j]
            if char not in node:
                return

            node = node[char]
            if trie.WORD in node:
                results.add(node[trie.WORD]) # node[''] contains the word

            board[i][j] = None
            if i > 0:
                backtrack(i-1, j, node)
            if i < m - 1:
                backtrack(i+1, j, node)
            if j > 0:
                backtrack(i, j-1, node)
            if j < n - 1:
                backtrack(i, j+1, node)
            board[i][j] = char

        for i in range(m):
            for j in range(n):
                backtrack(i, j, trie.root)

        return results
