        // Create a vector and copy set to it
            vector<string> ans;
            unordered_set<string>::iterator itr;
            for(itr=uset.begin(); itr!=uset.end(); itr++) {
                ans.push_back(*itr);
            }
        // return the vector
            return ans;
    }
};
