class Solution {
public:

    string str;
    bool vis[13][13];

    unordered_set<string> res;
    unordered_set<string> toFound;
    unordered_map<string, int> m;

    void rec(int i, int j, vector<vector<char>> &a)
    {
        if (toFound.find(str) != toFound.end())
        {
            string curr;

            for (auto x : str)
            {
                curr += x;
                m[curr]--;
            }

            toFound.erase(curr);
            res.insert(str);
        }

        if (i < 0 || j < 0 || i >= a.size() || j >= a[0].size() || vis[i][j] || m[str] == 0)
        {
            return;
        }

        vis[i][j] = 1;

        str += a[i][j];

        rec(i + 1, j, a);
        rec(i, j + 1, a);
        rec(i, j - 1, a);
        rec(i - 1, j, a);

        vis[i][j] = 0;

        str.pop_back();
    }

    vector<string> findWords(vector<vector<char>>& a, vector<string>& words) {


        for (auto x : words)
        {
            string curr;

            toFound.insert(x);

            for (auto y : x)
            {
                curr += y;
                m[curr]++;
