var findWords = function(board, words) {
    var i, j, tr;
    let used = new Array(board.length);
    let trie = {};
    let results = new Set();

    for( i of words ) {
        tr = trie;
        for( j=0; j<i.length; j++ ) {
            if( !(i[j] in tr) ) tr[i[j]] = {};
            tr = tr[i[j]];
        }
        tr.valid = i;
    }

    for( i=0; i<board.length; i++ ) {
        used[i] = new Array(board[0].length);
    }

    let search = function(i, j, trie) {
        var tr;
        if( i >= board.length || j >= board[0].length || i < 0 || j < 0 ) return;
        if( used[i][j] === true ) return;
        if( !(board[i][j] in trie) ) return;

        tr = trie[ board[i][j] ];
        used[i][j] = true;
        if( 'valid' in tr )
            results.add(tr.valid);
        search(i+1, j, tr);
        search(i-1, j, tr);
        search(i, j+1, tr);
        search(i, j-1, tr);
        used[i][j] = false;
    }

    for( i=0; i<board.length; i++ ) {
        for( j=0; j<board[0].length; j++ ) {
            search(i, j, trie);
        }
    }

    return [...results];
};
