**[C++] Simple solution for searching words on board using DFS & Trie:**

```
class TrieNode {
public:
    TrieNode* children[26] = {};
    bool isEnd = false;

    TrieNode() {}

    void addWord(string word) {
        TrieNode* curr = this;
        for(auto c: word) {
            if (!curr->children[c-'a']) curr->children[c-'a'] = new TrieNode();
            curr = curr->children[c-'a'];
        }
        curr->isEnd = true;
    }
};

class Solution {
public:
    vector<string> findWords(vector<vector<char>>& board, vector<string>& words) {
        TrieNode* dictionary = new TrieNode();
        for (string word: words) {
            dictionary->addWord(word);
        }

        int m = board.size(), n = board[0].size();
        vector<string> result;
        for(int i=0; i<m; i++) {
            for(int j=0; j<n; j++) {
                searchRecursively(board, i, j, dictionary, "", result);
            }
        }
        return result;
    }

    void searchRecursively(vector<vector<char>>& board, int x, int y, TrieNode* dict, string s, vector<string>& result) {
        int m = board.size(), n = board[0].size();
        if (board[x][y]=='#' || !dict->children[board[x][y]-'a']) return;

        char temp = board[x][y];
        board[x][y] = '#';

        if (dict->children[temp-'a']->isEnd) {
            dict->children[temp-'a']->isEnd = false;
            result.push_back(s+temp);
        }

        if(x-1>=0) searchRecursively(board, x-1, y, dict->children[temp-'a'], s+temp, result);
        if(x+1<m) searchRecursively(board, x+1, y, dict->children[temp-'a'], s+temp, result);
        if(y-1>=0) searchRecursively(board, x, y-1, dict->children[temp-'a'], s+temp, result);
        if(y+1<n) searchRecursively(board, x, y+1, dict->children[temp-'a'], s+temp, result);

        board[x][y] = temp;
    }
};
```

