class TrieNode:
    def __init__(self):
        self.child = collections.defaultdict(TrieNode)
        self.isEnd = False
        self.word = None

class Trie:
    def __init__(self):
        self.root = TrieNode()

    def insert(self, word):
        node = self.root
        for ch in word:
            node = node.child[ch]
        node.isEnd = True
        node.word = word

class Solution:
    def findWords(self, board: List[List[str]], words: List[str]) -> List[str]:
        if len(board) == 0 or len(words) == 0:
            return []

        trie = Trie()
        for word in words:
            trie.insert(word)

        res = []
        m, n = len(board), len(board[0])

        for i in range(m):
            for j in range(n):
                if board[i][j] in trie.root.child:
                    self.dfs(board, trie.root, m, n, i, j, res)
        return res

    def dfs(self, board, parent, m, n, x, y, res):
        ch = board[x][y]
        node = parent.child[ch]

        if node.isEnd:
            res.append(node.word)
            node.isEnd = False # avoid duplicates
            # Don't RETURN since child.word can be a prefix of other words, e.g., 'ane' and 'aneis'

        board[x][y] = "#"
        for new_x, new_y in [[x-1, y], [x+1, y], [x, y-1], [x, y+1]]:
            if 0 <= new_x < m and 0 <= new_y < n and board[new_x][new_y] in node.child:
                self.dfs(board, node, m, n, new_x, new_y, res)
        board[x][y] = ch

        # hit the end of trie leaf node, if already checking "abcd" in trie leaf, (whether it works or not)
        # we don't need to spend time checking "abcde" word 
        if not node.child:
            parent.child.pop(ch)
