class Solution:
    def findWords(self, board: List[List[str]], words: List[str]) -> List[str]:
        rows = len(board)
        cols = len(board[0])

        visited = set()
        results = set()

        # Hack. Reverse the string to look for.
        for i in range(len(words)):
            words[i] = words[i][::-1]

        # Normal dfs.
        def dfs(i, j, idx, word):
            if word in results   : return
            if i < 0 or rows <= i: return
            if j < 0 or cols <= j: return

            visited.add((i, j))

            if board[i][j] != word[idx]:
                pass

            elif idx == len(word)-1:
                results.add(word)

            else:
                if (i-1, j) not in visited: dfs(i-1, j, idx+1, word)
                if (i+1, j) not in visited: dfs(i+1, j, idx+1, word)
                if (i, j-1) not in visited: dfs(i, j-1, idx+1, word)
                if (i, j+1) not in visited: dfs(i, j+1, idx+1, word)

            visited.remove((i, j))

        # For every cell, search every word.
        for i in range(rows):
            for j in range(cols):
                for word in words:
                    if word not in results:
                        dfs(i, j, 0, word)

        return [word[::-1] for word in results]
