int rows[]={1,-1,0,0};
int cols[]={0,0,1,-1};
class node{
public:

	char ch;
	bool isTerminal;
	string name;
	unordered_map<char,node*> child;	
	node(char ch){
		this->ch=ch;
		isTerminal=false;
	}
};

class Trie{
public:

	node*root;
	Trie(){
		root=new node('\0');
	}
	void insert(string str){
		node*temp=root;
		for(int i=0;i<str.length();i++){
			char ch=str[i];
			if(temp->child[ch]!=0){
				temp=temp->child[ch];
			}else{
				temp->child[ch]=new node(ch);
				temp=temp->child[ch];
			}
		}
		temp->isTerminal=true;
		temp->name=str;
	}
};
class Solution {
public:

	bool visited[1000][1000]={false};
	void find_word(vector<vector<char>>&board,int i,int j,node*temp,set<string>&s){
		if(temp->isTerminal){
			if(i<0 or j<0 or i>=board.size() or j>=board[i].size() ){
				s.insert(temp->name);
				temp->isTerminal=false;
				return ;
			}
			if(temp->child.count(board[i][j])!=0){
				for(int k=0;k<4;k++){
					find_word(board,i+rows[k],j+cols[k],temp->child[board[i][j]],s);
				}
			}	
			s.insert(temp->name);
			temp->isTerminal=false;
		    return ;
		}
		if(i<0 or j<0 or i>=board.size() or j>=board[i].size() or visited[i][j]){
			return ;
		}
		char ch=board[i][j];
		visited[i][j]=true;
		if(temp->child.count(ch)==0){
			visited[i][j]=false;
			return ;
		}

		for(int k=0;k<4;k++){
			find_word(board,i+rows[k],j+cols[k],temp->child[ch],s);
		}
		visited[i][j]=false;
		return;
	}
    vector<string> findWords(vector<vector<char>>& board, vector<string>& words){
    	Trie t;
    	for(auto&it:words){
    		t.insert(it);
    	}
    	vector<string> ans;
    	set<string> s;
    	for(int i=0;i<board.size();i++){
    		for(int j=0;j<board[i].size();j++){
    			find_word(board,i,j,t.root,s);
    		}
    	}
    	for(auto&it:s){
    		ans.push_back(it);
    	}
    	return ans;
    }
};

