class Solution {

    fun findWords(board: Array<CharArray>, words: Array<String>): List<String> {

        if(board.isEmpty()) return listOf()
        if(words.all{ it.isEmpty() }) return listOf()

        val result = HashSet<String>()

        for(i in 0 until board.size){
            for(j in 0 until board[0].size){
                words
                    .filter{ it[0] == board[i][j] && dfs(board, i , j, it, 0)}
                    .forEach{
                        result.add(it)
                    }
            }
        } 
        return result.toList()
    }


    private fun dfs(board: Array<CharArray>, row: Int, col: Int,
                   word: String, index: Int): Boolean{

        if(row < 0 || row > board.lastIndex || col < 0 || col > board[0].lastIndex
                    || word[index] != board[row][col])
            return false

        if(index == word.lastIndex) return true

        val temp = board[row][col]
        board[row][col] = '#'

        val found = dfs(board, row + 1, col, word, index + 1)
                    || dfs(board, row - 1, col, word, index + 1)
                    || dfs(board, row, col + 1, word, index + 1)
                    || dfs(board, row, col - 1, word, index + 1)

        board[row][col] = temp
        return found
    }
}
