class Node:
    def __init__(self, prev):
        self.children = {}
        self.isWord = False
        self.prev = prev
class Trie:
    def __init__(self):
        self.root = Node(None)
### curNode keeps track of last character searched
        self.curNode = None

    def addWord(self,word):
        node = self.root
        for char in word:
            node = node.children.setdefault(char,Node(node))
        node.isWord = True

    def startsWith(self, prefix):
        node = self.curNode or self.root
        for char in prefix:
            if char not in node.children:
                return False
            node = node.children[char]
	### Once we've checked if a prefix is valid, we keep track of it with curNode
        self.curNode = node
        return True

    def remove(self, word):
        node = self.root
	### DFS to end of word, and only remove characters if they have no children AKA no other words start with the current prefix
        for char in word:
            if char not in node.children:
                return
            node = node.children[char]
    ### Set isWord to false, in case the word is a prefix to other words and we don't remove it
        node.isWord = False
        for char in word[::-1]:
            if not node.children:
                node = node.prev
                del node.children[char]


class Solution:
    def findWords(self, board: List[List[str]], words: List[str]) -> List[str]:
        self.trie = Trie()
	### Directions we can move to construct a word on the board
        self.dirs = [[1,0],[-1,0],[0,1],[0,-1]]
        self.ans = []
	### Add words to our Trie
        for word in words:
            self.trie.addWord(word)
	### Iterate through board and explore possible words that start with each character
        for r in range(len(board)):
            for c in range(len(board[r])):
                self.explore(board,[r,c],"")
        return self.ans


    def explore(self,board, pos, word):
        r, c = pos
	### Keep track of curNode to reset it back to this after we've finished searching
        curNode = self.trie.curNode
	### Make sure position is within board params
        if 0 <= r < len(board) and 0 <= c < len(board[r]):
	### board[r][c] is only None if we have visited it, will see this later
            if board[r][c] is None: return
	### Keep track of what the current word is 
            curWord = word + board[r][c]
	### Only continue exploring if the current word  continues to be a valid prefix
            if self.trie.startsWith(board[r][c]):
    ### If the curNode is the end of a word and the word has not been found yet, then we:
			# Add to our answer list
			# Remove the word from our Trie
                if self.trie.curNode.isWord:
                        self.ans.append(curWord)
                        self.trie.remove(curWord)
    ### Continue exploring and temporarily set the current position to None to indicate it has been explored
                temp = board[r][c]
                board[r][c] = None
	### Exploring in all directions
                for dir in self.dirs:
                    newPos = [pos[0] + dir[0], pos[1] + dir[1]]
                    self.explore(board, newPos, curWord)
	### After exploring in all directions, we reset board[r][c] so that it is no longer considered explored
	### Also reset the trie curNode because the current prefix is done being explored
                board[r][c] = temp
                self.trie.curNode = curNode
        return
