            return true;
        }

        visited_cells[i][j] = true;

        boolean top = (((i - 1) >= 0) && (word.charAt(index) == board[(i - 1)][j]) && (!visited_cells[(i - 1)][j]) && (recursive_function((i - 1) , j , rows , columns , board , visited_cells , (index + 1) , word , word_length)));

        boolean bottom = (((i + 1) < rows) && (word.charAt(index) == board[(i + 1)][j]) && (!visited_cells[(i + 1)][j]) && (recursive_function((i + 1) , j , rows , columns , board , visited_cells , (index + 1) , word , word_length)));

        boolean left = (((j - 1) >= 0) && (word.charAt(index) == board[i][(j - 1)]) && (!visited_cells[i][(j - 1)]) && (recursive_function(i , (j - 1) , rows , columns , board , visited_cells , (index + 1) , word , word_length)));

        boolean right = (((j + 1) < columns) && (word.charAt(index) == board[i][(j + 1)]) && (!visited_cells[i][(j + 1)]) && (recursive_function(i , (j + 1) , rows , columns , board , visited_cells , (index + 1) , word , word_length)));

        visited_cells[i][j] = false;

        return (top || bottom || left || right);
