        cols = len(board[0])

        def dfs(x, y, node, word):
            if (x,y) in visited or board[x][y] not in node.children:
                return

            visited.add((x,y))
            node = node.children[board[x][y]]
            word += board[x][y]

            if node.endofword:
                self.res.append(word)

            rrows = [-1, 0, 1, 0]
            ccols = [0, 1, 0, -1]
            for r,c in zip(rrows, ccols):
                if x+r >= 0 and y+c >= 0 and x+r < rows and y+c < cols:
                    dfs(x+r, y+c, node, word)

            visited.remove((x,y))

        for i in range(rows):
            for j in range(cols):
                if board[i][j] in root.children:
                    dfs(i, j, root, word)

