def dp(nums):
	A = [[0] * len(nums) for _ in range(len(nums))]
	for i in range(len(nums) - 1, -1, -1):
		for j in range(i, len(nums)):
			for k in range(i, j + 1):
				left = right = 0
				if k > i:
					left = A[i][k - 1]
				if k < j:
					right = A[k + 1][j]
				mid = nums[k]
				if i - 1 >= 0:
					mid *= nums[i - 1]
				if j + 1 < len(nums):
					mid *= nums[j + 1]
				A[i][j] = max(A[i][j], left + mid + right)
	return A[0][-1]
return dp(nums)
