class Solution {
public:
    vector<int>v;    // this is the new vector with new modification as [ 1 , oldArray , 1 ] because the question already said this if your i-1 or i +1 goes out of bound you have 1 to multiply with 
    int dp[505][505];
    //matrix-chain-multiplication
    int go( int si, int ei){
        if(dp[si][ei]!=-1)return dp[si][ei];
       if(si>ei)return 0;
       if(si==ei)return v[si-1]*v[si]*v[si+1];    //if you only have 1 element in your array
        int ans = INT_MIN;
       for(int k = si ; k<=ei ; k++){ 
		   //let suppose the last balloon is at kth position to 
		   //burst how will you burst this balloon? ofc with v[si-1] * v[ k ] * v[ ei +1 ] 
		   //now please dry run this step in your notebook 
		   //as v[ si -1 ] = v[ ei + 1 ] = 1 so basically it's 1 * v[k] * 1
		   int cur = v[si-1]*v[k]*v[ei+1]; 
           int pre = go(si,k-1);     // the cost from it's left part
           int post = go(k+1,ei); // the cost from it's right part 
           int total = cur+pre+post;  //total cost is the sum of all the combinations 
           ans = max(ans,total);  // store the maximum one 
       }
        return dp[si][ei] = ans;
    }
    int maxCoins(vector<int>& nums) {
        memset(dp,-1,sizeof(dp));
