    def maxCoins(self, nums):
        nums = [1] + [x for x in nums if x > 0] + [1]
        n = len(nums)
        dp = [[0] * n for _ in range(n)]
        for d in range(2, n):
            for i in range(0, n - d):
                j = i + d
                for k in range(i + 1, j):
                    last_burn = nums[i] * nums[k] * nums[j]
                    dp[i][j] = max(dp[i][j], dp[i][k] + dp[k][j] + last_burn)
        return dp[0][n - 1]
