class Solution {
    public int maxCoins(int[] nums) {
        ArrayList<Integer> arr=new ArrayList<>();
        for(int i :nums)
            arr.add(i);
        return getMaxCoins(arr,0);
    }

    public int getMaxCoins(ArrayList<Integer> arr,int ans){
        if(arr.size()==0)
            return ans;
        int max=0;
        for(int i=0;i<arr.size();i++){
            int mul=1,val=arr.get(i);
            if(i!=0) mul*=arr.get(i-1);
            if(i<arr.size()-1) mul*=arr.get(i+1);
            int sum=ans+val*mul;
            arr.remove(i);
            max=Math.max(max,getMaxCoins(arr,sum));
            arr.add(i,val);
        }
        return max;
    }
}
