     extended[0]=extended[n+1]=1;
     for(int i=1;i<n+1;i++)
         extended[i]=nums[i-1];

    int[][] dp = new int[n+2][n+2];
    for (int slider = 2; slider < n+ 2; slider++){
        for (int left = 0; left < n +2 - slider; left++) {
            int right = left + slider;
            for (int i = left + 1; i < right; i++)
                dp[left][right] = Math.max(dp[left][right],extended[left] * extended[i] * extended[right] 
                                           + dp[left][i] + dp[i][right]);

        }
    }

    return dp[0][n +1];
    }
}
